import React from "react";
import "./statistics.css";
import axios from "axios";
import ReactApexChart from "react-apexcharts";

const Statistics = () => {
  const arrayTemp = [];
  const arrayTDS = [];
  const arrayPH = [];
  const arrayTime = [];
  let seriesTemp = [];
  let seriesTDS = [];
  let seriesPH = [];
  axios
    .get("http://localhost:5000/api/data/chart")
    .then((response) => {
      const data = response.data;
      for (let j = 0; j < 48; j++) {
        var date = new Date(data.getalldata[j].createdAt);
        arrayTime.push(date.getTime());
        arrayTemp.push(data.getalldata[j].temperature * 1);
        arrayTDS.push(data.getalldata[j].TDS * 1);
        arrayPH.push(data.getalldata[j].PH * 1);
      }
      var values = [arrayTemp, arrayTDS, arrayPH, arrayTime];
      var i = 0;
      while (i < 48) {
        seriesTemp.push([values[3][i], values[0][i]]);
        seriesTDS.push([values[3][i], values[1][i]]);
        seriesPH.push([values[3][i], values[2][i]]);
        i++;
      }
      window.dispatchEvent(new Event("resize"));
    })
    .catch(() => {
      console.log("Error retrieving data!!!");
    });

  const seriesArea = [
    {
      name: "Temperature",
      data: seriesTemp,
    },
  ];
  const optionsArea = {
    chart: {
      id: "area",
      group: "social",
      type: "area",
    },
    colors: ["#00E396"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
    },
    title: {
      text: "Temperature Time Series Chart",
      align: "left",
      margin: 10,
      offsetX: 0,
      offsetY: 0,
      floating: false,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: undefined,
        color: "#263238",
      },
    },
  };

  const seriesSmall = [
    {
      name: "PH",
      data: seriesPH,
    },
  ];
  const optionsSmall = {
    chart: {
      id: "ig",
      group: "social",
      type: "area",
    },
    colors: ["#008FFB"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
      tickAmount: 6,
    },
    title: {
      text: "PH Time Series Chart",
      align: "left",
      margin: 10,
      offsetX: 0,
      offsetY: 0,
      floating: false,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: undefined,
        color: "#263238",
      },
    },
  };

  const seriesSmall2 = [
    {
      name: "TDS",
      data: seriesTDS,
    },
  ];
  const optionsSmall2 = {
    chart: {
      id: "li",
      group: "social",
      type: "area",
    },
    colors: ["#b37700"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
      tickAmount: 6,
    },
    title: {
      text: "TDS Time Series Chart",
      align: "left",
      margin: 10,
      offsetX: 0,
      offsetY: 0,
      floating: false,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: undefined,
        color: "#263238",
      },
    },
  };

  return (
    <div id="wrapper" className="wrapper">
      <div>
        <div id="chart-area">
          <ReactApexChart
            options={optionsArea}
            series={seriesArea}
            type="area"
            height={300}
            width={500}
          />
        </div>
      </div>

      <div className="columns">
        <div id="chart-small">
          <ReactApexChart
            options={optionsSmall}
            series={seriesSmall}
            type="area"
            height={300}
            width={500}
          />
        </div>
        <div id="chart-small2">
          <ReactApexChart
            options={optionsSmall2}
            series={seriesSmall2}
            type="area"
            height={200}
            width={500}
          />
        </div>
      </div>
    </div>
  );
};

export default Statistics;
