import React from "react";
import "./statistics.css";
import axios from "axios";
import ReactApexChart from "react-apexcharts";
import Select from "react-select";
import { url } from "../contexts/constants";
import { useState } from "react";

const Statistics = () => {
  const arrayTemp = [];
  const arrayTDS = [];
  const arrayPH = [];
  const arrayTime = [];
  let seriesTemp = [];
  let seriesTDS = [];
  let seriesPH = [];
  let length;

  const [time, setTime] = useState();
  //var time = "3";
  if (time === "1") {
    fetchChartData(48);
  } else if (time === "2") {
    fetchChartData(336);
  } else if (time === "3") {
    fetchChartData(1440);
  }
  function fetchChartData(length) {
    axios
      .get(`${url}/data/chart`)
      .then((response) => {
        const data = response.data;

        for (let j = 0; j < length; j++) {
          var date = new Date(data.getalldata[j].createdAt);
          arrayTime.push(date.getTime());
          arrayTemp.push(data.getalldata[j].temperature * 1);
          arrayTDS.push(data.getalldata[j].TDS * 1);
          arrayPH.push(data.getalldata[j].PH * 1);
        }
        var values = [arrayTemp, arrayTDS, arrayPH, arrayTime];
        var i = 0;
        while (i < length) {
          seriesTemp.push([values[3][i], values[0][i]]);
          seriesTDS.push([values[3][i], values[1][i]]);
          seriesPH.push([values[3][i], values[2][i]]);
          i++;
        }
        window.dispatchEvent(new Event("resize"));
      })
      .catch(() => {
        console.log("Error retrieving data!!!");
      });
  }

  const seriesArea = [
    {
      name: "Temperature",
      data: seriesTemp,
    },
  ];
  const optionsArea = {
    chart: {
      id: "area",
      group: "social",
      type: "area",
    },
    colors: ["#00E396"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
    },
    title: {
      text: "Temperature Time Series Chart",
      align: "left",
      margin: 10,
      offsetX: 0,
      offsetY: 0,
      floating: false,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: undefined,
        color: "#263238",
      },
    },
    zoom: {
      type: "x",
      enabled: true,
      autoScaleYaxis: true,
    },
    toolbar: {
      autoSelected: "zoom",
    },
  };

  const seriesSmall = [
    {
      name: "PH",
      data: seriesPH,
    },
  ];
  const optionsSmall = {
    chart: {
      id: "ig",
      group: "social",
      type: "area",
    },
    colors: ["#008FFB"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
      tickAmount: 6,
    },
    title: {
      text: "PH Time Series Chart",
      align: "left",
      margin: 10,
      offsetX: 0,
      offsetY: 0,
      floating: false,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: undefined,
        color: "#263238",
      },
    },
    zoom: {
      type: "x",
      enabled: true,
      autoScaleYaxis: true,
    },
    toolbar: {
      autoSelected: "zoom",
    },
  };

  const seriesSmall2 = [
    {
      name: "TDS",
      data: seriesTDS,
    },
  ];
  const optionsSmall2 = {
    chart: {
      id: "li",
      group: "social",
      type: "area",
    },
    colors: ["#b37700"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
      tickAmount: 6,
    },
    title: {
      text: "TDS Time Series Chart",
      align: "left",
      margin: 10,
      offsetX: 0,
      offsetY: 0,
      floating: false,
      style: {
        fontSize: "14px",
        fontWeight: "bold",
        fontFamily: undefined,
        color: "#263238",
      },
    },
    zoom: {
      type: "x",
      enabled: true,
      autoScaleYaxis: true,
    },
    toolbar: {
      autoSelected: "zoom",
    },
  };
  const options = [
    { value: "1", label: "1 day" },
    { value: "2", label: "1 week" },
    { value: "3", label: "1 month" },
  ];

  const onChange = (event) => setTime(this.state.value);

  return (
    <>
      <div id="wrapper" className="wrapper">
        <Select
          options={options}
          className="selecttime"
          placeholder={<div>Select time</div>}
          onChange={onChange}
          value={this.state.value}
        />
        <div className="chart">
          <div id="chart-small">
            <ReactApexChart
              options={optionsSmall}
              series={seriesSmall}
              type="area"
              height={300}
              width={370}
            />
          </div>
          <div id="chart-small2">
            <ReactApexChart
              options={optionsSmall2}
              series={seriesSmall2}
              type="area"
              height={300}
              width={370}
            />
          </div>

          <div>
            <div id="chart-area">
              <ReactApexChart
                options={optionsArea}
                series={seriesArea}
                type="area"
                height={300}
                width={370}
              />
            </div>
          </div>
        </div>
      </div>
      <div className="wrapper"></div>
    </>
  );
};

export default Statistics;
