import {
  DATA_LOADED_FAIL,
  DATA_LOADED_SUCCESS,
  ADD_DEVICE,
  CONFIG,
  STATE_LOADED_FAIL,
  STATE_LOADED_SUCCESS,
  ADD_CONFIGTYPE,
  DELETE_CONFIGTYPE,
  FIND_CONFIGTYPE,
  UPDATE_CONFIGTYPE,

} from "../contexts/constants";
export const dataReducer = (state, action) => {
  const { type, payload } = action;
  switch (type) {
    case DATA_LOADED_SUCCESS:
      return {
        ...state,
        data: payload,
        dataLoading: false,
      };
    case DATA_LOADED_FAIL:
      return {
        ...state,
        data: [],
        dataLoading: false,
      };
    case STATE_LOADED_SUCCESS:
      return {
        ...state,
        state: payload,
        dataLoading: false,
      };
    case STATE_LOADED_FAIL:
      return {
        ...state,
        state: [],
        dataLoading: false,
      };
    case ADD_DEVICE:
      return {
        ...state,
      };
    case CONFIG:
      return {
        ...state,
      };

    case ADD_CONFIGTYPE:
      return {
        ...state,
        posts: [...state.posts, payload],
      };

    case DELETE_CONFIGTYPE:
      return {
        ...state,
        posts: state.posts.filter((post) => post._id !== payload),
      };

    case FIND_CONFIGTYPE:
      return { ...state, post: payload };

    case UPDATE_CONFIGTYPE:
      const newPosts = state.posts.map((post) =>
        post._id === payload._id ? payload : post
      );

      return {
        ...state,
        posts: newPosts,
      };

    default:
      return state;
  }
};
