import React, { useRef, useState } from "react";
import { AuthContext } from "../contexts/AuthContext";
import { useContext, useEffect } from "react";
import image from "../assets/iot.jpg";
import { DataContext } from "../contexts/DataContext";
import { StatusContext } from "../contexts/StatusContext";
import { TotalVolumeContext } from "../contexts/TotalVolumeContext";
import { SetVolumeContext } from "../contexts/SetVolumeContext";
import "./views.css";
import dosingmachine from "../assets/dosingmachine.png";
import { io } from "socket.io-client";

import PHicon from "../assets/PHlogo.png";
import tempIcon from "../assets/temp.png";
import tdsIcon from "../assets/tds.png";

import { CircularProgressbar } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const Dashboard = () => {
  //get username
  const {
    authState: {
      user: { username, _id },
    },
  } = useContext(AuthContext);

  const [data, setData] = useState({ data: [] });
  const {
    dataState: {
      data: { temperature, TDS, PH, createdAt },

      dataLoading,
    },
    getData,
  } = useContext(DataContext);

  const [status, setStatus] = useState({ status: [] });
  const {
    statusState: {
      status: { state },
    },
    getStatus,
  } = useContext(StatusContext);

  const {
    setVolumeState: {
      setVolume: { Nutri_A_full, Nutri_B_full, Acid_So_full, Base_So_full },
    },
    getSetVolume,
  } = useContext(SetVolumeContext);

  const {
    totalVolumeState: {
      totalVolume: { Nutri_A, Nutri_B, Water, Acid_So, Base_So },
    },
    getTotalVolume,
  } = useContext(TotalVolumeContext);

  const [selectTypeState, setSelectTypeState] = useState();

  // //get last data
  useEffect(() => getData(), [data]);
  useEffect(() => getStatus(), [state]);
  useEffect(() => getTotalVolume(), []);
  useEffect(() => getSetVolume(), []);

  const socket = useRef();

  useEffect(() => {
    socket.current = io("ws://localhost:5200", {
      reconnection: true,
      reconnectionDelay: 500,
      reconnectionAttempts: 10,
    });
    socket.current.on();
  }, []);

  useEffect(() => {
    socket.current.emit("addUser", _id);
    socket.current.on("getUsers", (users) => {
      console.log(users);
    });
    socket.current.emit("getData", _id);
    socket.current.on("sendData", (data) => {
      setData(data);
    });
    socket.current.emit("getState", _id);
    socket.current.on("sendState", (sendState) => {
      setStatus(sendState);
    });
  }, [username]);

  let state_style;
  if (state === "offline") {
    state_style = { backgroundColor: "grey" };
  } else if (state === "online") {
    state_style = { backgroundColor: "#4d94ff" };
  }
  const percentage = 66;
  var nutriA = Nutri_A_full - Nutri_A;
  var rounded;
  function getPercent(value_full, value) {
    var num = ((value_full - value) / value_full) * 100;
    var roundedString = num.toFixed(0);
    rounded = Number(roundedString);
    return rounded;
  }
  var Nutri_A_percent = getPercent(Nutri_A_full, Nutri_A);
  var Nutri_B_percent = getPercent(Nutri_B_full, Nutri_B);
  var Acid_percent = getPercent(Acid_So_full, Acid_So);
  var Nutri_A_percent = getPercent(Base_So_full, Base_So);

  console.log(Nutri_A_percent);
  return (
    <>
      <main>
        <small className="text-mute">Last Time Update: {createdAt}</small>
        <div className="insights">
          <div className="PH-TDS">
            <div className="PH">
              <img src={PHicon} className="icon-sen" />
              <div className="middle">
                <div className="left">
                  <h3>PH</h3>
                  <h1>{PH}</h1>
                </div>
                <small className="text-muted">Time update</small>
              </div>
            </div>
            <div className="TDS">
              <img src={tdsIcon} className="icon-sen" />
              <div className="middle">
                <div className="left">
                  <h3>TDS</h3>
                  <h1>{TDS}</h1>
                </div>
                <small className="text-muted">PPM</small>
              </div>
            </div>
          </div>
          <div className="temperature">
            <img src={tempIcon} className="icon-sen" />
            <div className="middle">
              <div className="left">
                <h3>Temperature</h3>
                <h1>{temperature} </h1>
                <small className="text-muted">°C</small>
              </div>
            </div>
          </div>
        </div>
        <CircularProgressbar
          value={Nutri_A_full - Nutri_A}
          maxValue={Nutri_A_full}
          text={Nutri_A_percent}
        />
        ;<div>Current Nutri_A left {Nutri_A} ml</div>
        <div>Current Nutri_B left {Nutri_B} ml</div>
        <div>Current Acid_So left {Acid_So} ml</div>
        <div>Current Base_So {Base_So} ml</div>
        <div>Current Nutri_A_full left {Nutri_A_full} ml</div>
        <div>Current Nutri_B_full left {Nutri_B_full} ml</div>
        <div>Current Acid_So_full left {Acid_So_full} ml</div>
        <div>Current Base_So_full {Base_So_full} ml</div>
      </main>
      <div className="right">
        <div className="top">
          <button id="menu-btn1">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              className="bi bi-list"
              viewBox="0 0 16 16"
            >
              <path
                fillRule="evenodd"
                d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5z"
              />
            </svg>
          </button>
        </div>
        <div className="userPart">
          <div className="user">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="50"
              height="50"
              fill="currentColor"
              className="user-icon"
              viewBox="0 0 16 16"
            >
              <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0z" />
              <path
                fillRule="evenodd"
                d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8zm8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1z"
              />
            </svg>
            <div className="userName">Welcome {username}</div>
          </div>
          <div className="userDevice">
            <div>My Devices</div>
            <div className="devices">
              <div className="dosingmachine">
