import React from "react";
import "./statistics.css";
import axios from "axios";
import ReactApexChart from "react-apexcharts";
import Select from "react-select";
import { url } from "../contexts/constants";
import { useState, useEffect, useContext } from "react";
import { DeviceContext } from "../contexts/DeviceContext";

const Statistics = () => {
  const arrayTime = [];
  let seriesTemp = [];
  let seriesTDS = [];
  let seriesPH = [];
  const [selectedDevice, setSelectDevice] = useState("");

  const {
    deviceState: { deviceData },
    getDevice,
  } = useContext(DeviceContext);
  // //get last data

  const [chartData, setChartData] = useState({
    seriesTemperature: [
      {
        name: "Temperature",
        data: [],
      },
    ],
    optionsTemperature: {
      chart: {
        id: "area",
        group: "social",
        type: "area",
      },
      colors: ["#00E396"],
      xaxis: {
        type: "datetime",
        categories: [],
      },
      noData: {
        text: "Loading...",
      },
    },
    seriesPHChart: [
      {
        name: "PH",
        data: [],
      },
    ],
    optionsPhChart: {
      chart: {
        id: "ig",

        group: "social",
        type: "area",
      },
      colors: ["#008FFB"],
      xaxis: {
        type: "datetime",
        categories: [],
        tickAmount: 6,
      },
      noData: {
        text: "Loading...",
      },
    },

    seriesTDSChart: [
      {
        name: "TDS",
        data: [],
      },
    ],
    optionsTDSChart: {
      chart: {
        id: "li",
        group: "social",
        type: "area",
      },
      colors: ["#b37700"],
      xaxis: {
        type: "datetime",
        categories: [],
        tickAmount: 6,
      },
      noData: {
        text: "Loading...",
      },
    },
  });

  useEffect(() => {
    getDevice();

    if (time === "1") {
      console.log(selectedDevice);
      fetchChartData(selectedDevice, 48);
    } else if (time === "2") {
      console.log(selectedDevice);
      fetchChartData(selectedDevice, 336);
    } else if (time === "3") {
      console.log(selectedDevice);
      fetchChartData(selectedDevice, 1440);
    }
  }, []);

  useEffect(() => {
    if (deviceState) {
      for (let i = 0; i < deviceData.length; i++) {
        if (selectDevice === optionsDevice[i].label) {
          setSelectDevice(deviceData[i].device);
        }
      }
    }
  }, [deviceState]);

  var optionsDevice = [];
  for (let i = 0; i < deviceData.length; i++) {
    let value = [];
    value = { value: deviceData[i].device, label: `Device ${i + 1}` };
    optionsDevice.push(value);
  }
  console.log(optionsDevice);

  const [selectDevice, setselectDevice] = useState("Device 2");
  const OnChangeDevice = (event) => {
    setselectDevice(event.label);
  };
  console.log(selectDevice);

  const [time, setTime] = useState("1");
  // if (time === "1") {
  //   console.log(selectedDevice);
  //   fetchChartData("58bf251706a0", 48);
  // } else if (time === "2") {
  //   console.log(selectedDevice);
  //   fetchChartData(selectedDevice, 336);
  // } else if (time === "3") {
  //   console.log(selectedDevice);
  //   fetchChartData(selectedDevice, 1440);
  // }
  function fetchChartData(device, length) {
    axios
      .get(`${url}/data/chart/${device}/${length}`)
      .then((response) => {
        const data = response.data;
        console.log(data);
        seriesTemp.push(...data.getalldata[0]);
        seriesTDS.push(...data.getalldata[1]);
        seriesPH.push(...data.getalldata[2]);
        arrayTime.push(...data.getalldata[3]);

        setChartData({
          seriesTemp: [
            {
              name: "Temperature",
              data: seriesTemp,
            },
          ],
          optionsTemp: {
            chart: {
              id: "area",
              group: "social",
              type: "area",
            },
            colors: ["#00E396"],
            xaxis: {
              type: "datetime",
              categories: arrayTime,
            },
          },
          seriesPH: [
            {
              name: "PH",
              data: seriesPH,
            },
          ],
          optionsPH: {
            chart: {
              id: "ig",

              group: "social",
              type: "area",
            },
            colors: ["#008FFB"],
            xaxis: {
              type: "datetime",
              categories: arrayTime,
              tickAmount: 6,
            },
          },

          seriesTDS: [
            {
              name: "TDS",
              data: seriesTDS,
            },
          ],
          optionsTDS: {
            chart: {
              id: "li",
              group: "social",
              type: "area",
            },
            colors: ["#b37700"],
            xaxis: {
              type: "datetime",
              categories: arrayTime,
              tickAmount: 6,
            },
          },
        });

        window.dispatchEvent(new Event("resize"));
      })
      .catch((e) => {
        console.log("Error retrieving data!!!");
      });
  }

  useEffect(() => {
    if (time === "1") {
      console.log(selectedDevice);
      fetchChartData(selectedDevice, 48);
    } else if (time === "2") {
      console.log(selectedDevice);
      fetchChartData(selectedDevice, 336);
    } else if (time === "3") {
      console.log(selectedDevice);
      fetchChartData(selectedDevice, 1440);
    }
  }, [selectDevice, time]);

  // const seriesTemperature = [
  //   {
  //     name: "Temperature",
  //     data: seriesTemp,
  //   },
  // ];
  // const optionsTemperature = {
  //   chart: {
  //     id: "area",
  //     group: "social",
  //     type: "area",
  //   },
  //   colors: ["#00E396"],
  //   xaxis: {
  //     type: "datetime",
  //     min: arrayTime[0],
  //   },
  //   zoom: {
  //     type: "x",
  //     enabled: true,
  //     autoScaleYaxis: true,
  //   },
  //   toolbar: {
  //     autoSelected: "zoom",
  //   },
  // };

  // const seriesPHChart = [
  //   {
  //     name: "PH",
  //     data: seriesPH,
  //   },
  // ];
  // const optionsPhChart = {
  //   chart: {
  //     id: "ig",
  //     group: "social",
  //     type: "area",
  //   },
  //   colors: ["#008FFB"],
  //   xaxis: {
  //     type: "datetime",
  //     min: arrayTime[0],
  //     tickAmount: 6,
  //   },
  //   zoom: {
  //     type: "x",
  //     enabled: true,
  //     autoScaleYaxis: true,
  //   },
  //   toolbar: {
  //     autoSelected: "zoom",
  //   },
  // };

  // const seriesTDSChart = [
  //   {
  //     name: "TDS",
  //     data: seriesTDS,
  //   },
  // ];
  // const optionsTDSChart = {
  //   chart: {
  //     id: "li",
  //     group: "social",
  //     type: "area",
  //   },
  //   colors: ["#b37700"],
  //   xaxis: {
  //     type: "datetime",
  //     min: arrayTime[0],
  //     tickAmount: 6,
  //   },
  //   zoom: {
  //     type: "x",
  //     enabled: true,
  //     autoScaleYaxis: true,
  //   },
  //   toolbar: {
  //     autoSelected: "zoom",
  //   },
  // };
  const options = [
    { value: "1", label: "1 day" },
    { value: "2", label: "1 week" },
    { value: "3", label: "1 month" },
  ];

  const onChange = (event) => setTime(event.value);

  return (
    <>
      <div id="wrapper" className="wrapper">
        <Select
          options={optionsDevice}
          className="selecttime"
          placeholder={<div>{selectDevice}</div>}
          onChange={OnChangeDevice}
        />
        <Select
          options={options}
          className="selecttime"
          placeholder={<div>1 day</div>}
          onChange={onChange}
        />
        <div className="chart-time-series">
          <div id="chart-small" className="timeseries">
            <ReactApexChart
              options={chartData.optionsPhChart}
              series={chartData.seriesPHChart}
              type="area"
              height={170}
              width={1040}
            />
            <h3>PH Time Series Chart</h3>
          </div>
          <div id="chart-small2" className="timeseries">
            <ReactApexChart
              options={chartData.optionsTDSChart}
              series={chartData.seriesTDSChart}
              type="area"
              height={170}
              width={1040}
            />
            <h3>TDS Time Series Chart</h3>
          </div>

          <div>
            <div id="chart-area" className="timeseries">
              <ReactApexChart
                options={chartData.optionsTemperature}
                series={chartData.seriesTemperature}
                type="area"
                height={170}
                width={1040}
              />
              <h3>Temperature Time Series Chart</h3>
            </div>
          </div>
        </div>
      </div>
      <div className="wrapper"></div>
    </>
  );
};

export default Statistics;
