import { useState, useContext, useEffect, useRef } from "react";
import "./device.css";
import { DataContext } from "../contexts/DataContext";
import { TypeContext } from "../contexts/TypeContext";
import { ConfigContext } from "../contexts/ConfigContext";
import { TotalVolumeContext } from "../contexts/TotalVolumeContext";
import { AuthContext } from "../contexts/AuthContext";
import AlertMessage from "../components/layout/AlertMessage";
import devicelogo from "../assets/device.jpg";
import PHImage from "../assets/PH.png";
import React from "react";
import { io } from "socket.io-client";
import { SetVolumeContext } from "../contexts/SetVolumeContext";
const Device = () => {
  const {
    dataState: {
      data: { device },
    },
    getData,
  } = useContext(DataContext);

  const [data, setData] = useState({ data: [] });

  const {
    totalVolumeState: {
      totalVolume: { Nutri_A, Nutri_B, Water, Acid_So, Base_So },
    },
    getTotalVolume,
  } = useContext(TotalVolumeContext);

  const {
    configState: {
      currentConfig: { TDS, dead_TDS, PH, dead_PH, nutri_Ratio },
    },
    getConfigData,
  } = useContext(ConfigContext);

  const {
    setVolumeState: {
      setVolume: { Nutri_A_full, Nutri_B_full, Acid_So_full, Base_So_full },
    },
    getSetVolume,
  } = useContext(SetVolumeContext);

  useEffect(() => getData(), []);
  useEffect(() => getTotalVolume(), []);
  useEffect(() => getConfigData(), []);
  useEffect(() => getSetVolume(), []);
  console.log(Nutri_A_full, Nutri_B_full, Acid_So_full, Base_So_full);
  // add device

  const { addDevices } = useContext(DataContext);

  const [newDevice, setNewDevice] = useState({
    device: "",
  });

  const { deviceID } = newDevice;

  const [alert, setAlert] = useState(null);

  const onChangeNewDeviceForm = (event) =>
    setNewDevice({
      ...newDevice,
      [event.target.name]: event.target.value,
    });

  const onSubmmit = async (event) => {
    event.preventDefault();

    try {
      const { message } = await addDevices(newDevice);
      setAlert({ type: "danger", message: message });
      setTimeout(() => setAlert(null), 5000);
      setNewDevice({ deviceID: "" });
    } catch (error) {
      console.log(error);
    }
  };
  // config data

  const { config } = useContext(DataContext);

  const [alert_conf, setAlert_conf] = useState(null);

  const [newConfig, newSetConfig] = useState({
    TDS: "",
    dead_TDS: "",
    PH: "",
    dead_PH: "",
    nutri_Ratio: "",
  });

  const { newTDS, newDead_TDS, newPH, newDead_PH, newnutri_Ratio } = newConfig;

  const onSubmmitConf = async (event) => {
    event.preventDefault();

    try {
      const { message } = await config(newConfig);
      setAlert_conf({ type: "danger", message: message });
      setTimeout(() => setAlert_conf(null), 5000);
      newSetConfig({
        TDS: " ",
        dead_TDS: " ",
        PH: " ",
        dead_PH: " ",
        nutri_Ratio: " ",
      });
    } catch (error) {
      console.log(error);
    }
  };

  const onChangeNewSetConfigForm = (event) =>
    newSetConfig({
      ...newConfig,
      device,
      type: "Manual",
      [event.target.name]: event.target.value,
    });

  //socket io
  const {
    authState: {
      user: { username, _id },
    },
  } = useContext(AuthContext);
  const socket = useRef();

  useEffect(() => {
    socket.current = io("ws://localhost:5200", {
      reconnection: true,
      reconnectionDelay: 500,
      reconnectionAttempts: 10,
    });
    socket.current.on();
  }, []);

  useEffect(() => {
    socket.current.emit("addUser", _id);
    socket.current.on("getUsers", (users) => {
      console.log(users);
    });
    socket.current.emit("getConfig", _id);
    socket.current.on("sendConfig", (config) => {
      console.log(config);
      setData(config);
    });
  }, [username]);

  return (
    <>
      <div className="main">
        <div className="config">
          <h1>Config value</h1>

          <form onSubmit={onSubmmitConf}>
            <div className="PH-config">
              <div className="pH">
                <h2>Current PH value set: {PH}</h2>
                <input
                  type="number"
                  step="0.01"
                  className="text"
                  placeholder="Set PH value..."
                  name="PH"
                  value={newPH}
                  onChange={onChangeNewSetConfigForm}
                  required
                />
              </div>
              <div className="dead_PH">
                <h2>Current dead PH value set: {dead_PH}</h2>
                <input
                  type="number"
                  step="0.01"
                  className="text"
                  placeholder="Dead PH value..."
                  name="dead_PH"
                  value={newDead_PH}
                  onChange={onChangeNewSetConfigForm}
                  required
                />
              </div>
            </div>

            <div className="TDS-config">
              <div className="tDS">
                <h2>Current TDS value set: {TDS} ppm</h2>
                <input
                  type="number"
                  step="0.01"
                  className="text"
                  placeholder="Set TDS value..."
                  name="TDS"
                  value={newTDS}
                  onChange={onChangeNewSetConfigForm}
                  required
                />
              </div>
              <div className="dead_TDS">
                <h2>Current dead TDS value set: {dead_TDS} ppm</h2>
                <input
                  type="number"
                  step="0.01"
                  className="text"
                  placeholder="Dead TDS value..."
                  name="dead_TDS"
                  value={newDead_TDS}
                  onChange={onChangeNewSetConfigForm}
                  required
                />
              </div>
            </div>
            {/* <img src={PHImage} /> */}
            <div className="nutriRatio">
              <h2>Current nutri ratio value set: {nutri_Ratio} </h2>
              <input
                type="number"
                step="0.01"
                className="text"
                placeholder="Nutri ratio value..."
                name="nutri_Ratio"
                value={newnutri_Ratio}
                onChange={onChangeNewSetConfigForm}
                required
              />
            </div>
            <AlertMessage info={alert_conf} className="message" />
            <input type="submit" className="button" value="Submit" />
          </form>
          <div>Current Nutri_A left {Nutri_A} ml</div>
          <div>Current Nutri_B left {Nutri_B} ml</div>
          <div>Current Acid_So left {Acid_So} ml</div>
          <div>Current Base_So {Base_So} ml</div>
          <div>Current Nutri_A_full left {Nutri_A_full} ml</div>
          <div>Current Nutri_B_full left {Nutri_B_full} ml</div>
          <div>Current Acid_So_full left {Acid_So_full} ml</div>
          <div>Current Base_So_full {Base_So_full} ml</div>
        </div>
      </div>

      <div className="addDevice">
        <div className="device-box">
          <img src={devicelogo} />
          <div className="form">
            <h1>Add your new device</h1>
            <form onSubmit={onSubmmit}>
              <input
                type="text"
                className="text"
                placeholder="Device ID..."
                name="device"
                value={deviceID}
                onChange={onChangeNewDeviceForm}
                required
              />
              <AlertMessage info={alert} />
              <input type="submit" className="addButton" value="Add device  " />
            </form>
          </div>
        </div>
      </div>
    </>
  );
};
export default Device;
