import { useState, useContext, useEffect, useRef } from "react";
import "./device.css";
import { DataContext } from "../contexts/DataContext";
import { TypeContext } from "../contexts/TypeContext";
import { ConfigContext } from "../contexts/ConfigContext";
import { TotalVolumeContext } from "../contexts/TotalVolumeContext";
import { AuthContext } from "../contexts/AuthContext";
import AlertMessage from "../components/layout/AlertMessage";
import devicelogo from "../assets/device.jpg";
import PHImage from "../assets/PH.png";
import React from "react";
import { io } from "socket.io-client";
import { SetVolumeContext } from "../contexts/SetVolumeContext";
const Device = () => {
  const {
    dataState: {
      data: { device },
    },
    getData,
  } = useContext(DataContext);

  const [data, setData] = useState({ data: [] });

  const {
    totalVolumeState: {
      totalVolume: { Nutri_A, Nutri_B, Water, Acid_So, Base_So },
    },
    getTotalVolume,
  } = useContext(TotalVolumeContext);

  const {
    configState: {
      currentConfig: { type, TDS, dead_TDS, PH, dead_PH, nutri_Ratio },
    },
    getConfigData,
  } = useContext(ConfigContext);

  const {
    setVolumeState: {
      setVolume: { Nutri_A_full, Nutri_B_full, Acid_So_full, Base_So_full },
    },
    getSetVolume,
  } = useContext(SetVolumeContext);

  useEffect(() => getData(), []);
  useEffect(() => getTotalVolume(), []);
  useEffect(() => getConfigData(), []);
  useEffect(() => getSetVolume(), []);
  console.log(Nutri_A_full, Nutri_B_full, Acid_So_full, Base_So_full);
  // add device

  const { addDevices } = useContext(DataContext);

  const [newDevice, setNewDevice] = useState({
    device: "",
  });

  const { deviceID } = newDevice;

  const [alert, setAlert] = useState(null);

  const onChangeNewDeviceForm = (event) =>
    setNewDevice({
      ...newDevice,
      [event.target.name]: event.target.value,
    });

  const onSubmmit = async (event) => {
    event.preventDefault();

    try {
      const { message } = await addDevices(newDevice);
      setAlert({ type: "danger", message: message });
      setTimeout(() => setAlert(null), 5000);
      setNewDevice({ deviceID: "" });
    } catch (error) {
      console.log(error);
    }
  };
  // config data

  const { config } = useContext(DataContext);

  const [alert_conf, setAlert_conf] = useState(null);

  const [newConfig, newSetConfig] = useState({
    TDS: "",
    dead_TDS: "",
    PH: "",
    dead_PH: "",
    nutri_Ratio: "",
  });

  const { newTDS, newDead_TDS, newPH, newDead_PH, newnutri_Ratio } = newConfig;

  const onSubmmitConf = async (event) => {
    event.preventDefault();

    try {
      const { message } = await config(newConfig);
      setAlert_conf({ type: "danger", message: message });
      setTimeout(() => setAlert_conf(null), 5000);
      newSetConfig({
        TDS: " ",
        dead_TDS: " ",
        PH: " ",
        dead_PH: " ",
        nutri_Ratio: " ",
      });
    } catch (error) {
      console.log(error);
    }
  };

  const onChangeNewSetConfigForm = (event) =>
    newSetConfig({
      ...newConfig,
      device,
      type: "Manual",
      [event.target.name]: event.target.value,
    });

  //socket io
  const {
    authState: {
      user: { username, _id },
    },
  } = useContext(AuthContext);
  const socket = useRef();

  useEffect(() => {
    socket.current = io("ws://localhost:5200", {
      reconnection: true,
      reconnectionDelay: 500,
      reconnectionAttempts: 10,
    });
    socket.current.on();
  }, []);

  useEffect(() => {
    socket.current.emit("addUser", _id);
    socket.current.on("getUsers", (users) => {
      console.log(users);
    });
    socket.current.emit("getConfig", _id);
    socket.current.on("sendConfig", (config) => {
      console.log(config);
      setData(config);
    });
  }, [username]);

  let body1 = null;
  if (configLoading) {
    body1 = (
      <>
        <h1>Loading</h1>
      </>
    );
  } else if (config.length === 0) {
    body1 = (
      <>
        <h3 className="title-new-vegetable">
          Click button below to add a new vegetables
        </h3>
        <Button
          className="button-new-vegetable"
          variant="primary"
          onClick={setShowAddTypeModal.bind(this, true)}
        >
          Add new vegetables
        </Button>
      </>
    );
  } else {
    body1 = (
      <>
        {/* <Select
          options={options}
          onChange={(e) => {
            console.log(e);
            const typeState = e.value;
            setSelectTypeState(typeState);
          }}
        /> */}

        <div className="title-new-vegetable">Current setting is {type}</div>
        <div className="singleType">
          <div className="valueName">
            <div className="typeName">Type</div>
            <div>TDS</div>
            <div className="dead_TDSName">dead TDS</div>
            <div className="PHName">PH</div>
