import {
  CONFIGTYPE_LOADED_SUCCESS,
  CONFIGTYPE_LOADED_FAIL,
  ADD_CONFIGTYPE,
  DELETE_CONFIGTYPE,
  FIND_CONFIGTYPE,
  UPDATE_CONFIGTYPE,
} from "../contexts/constants";
export const typeReducer = (type, action) => {
  const { Type, payload } = action;
  switch (Type) {
    case CONFIGTYPE_LOADED_SUCCESS:
      return {
        ...type,
        config: payload,
        configLoading: true,
      };
    case CONFIGTYPE_LOADED_FAIL:
      return {
        ...type,
        config: [],
        configLoading: false,
      };
    case ADD_CONFIGTYPE:
      return {
        ...type,
        posts: [...type.posts, payload],
      };

    case DELETE_CONFIGTYPE:
      return {
        ...type,
        posts: type.posts.filter((post) => post._id !== payload),
      };

    case FIND_CONFIGTYPE:
      return { ...type, post: payload };

    case UPDATE_CONFIGTYPE:
      const newPosts = type.posts.map((post) =>
        post._id === payload._id ? payload : post
      );

      return {
        ...type,
        posts: newPosts,
      };

    default:
      return type;
  }
};
