import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { useContext, useEffect, useState } from "react";
import { TypeContext } from "../contexts/TypeContext";
const UpdateTypeModal = () => {
  const {
    typeState: { configType },
    showUpdateTypeModal,
    setShowUpdateTypeModal,
    updateConfigType,
    setShowToast,
  } = useContext(TypeContext);

  //const [updatedType, setUpdatedType] = useState(configType);
  const [updatedPost, setUpdatedPost] = useState(configType);
  useEffect(() => setUpdatedType(configType), [configType]);

  const { type, TDS, dead_TDS, PH, dead_PH, nutri_Ratio } = updatedPost;

  const onChangeUpdatedTypeForm = (event) =>
    setUpdatedPost({ ...updatedPost, [event.target.name]: event.target.value });

  //   const resetAndAddTypeData = () => {
  //     setNewType({
  //       type: "",
  //       TDS: "",
  //       dead_TDS: "",
  //       PH: "",
  //       dead_PH: "",
  //       nutri_Ratio: "",
  //     });
  //     setShowAddTypeModal(false);
  //   };
  //   const closeDialog = () => {
  //     resetAndAddTypeData();
  //   };

  const onSubmit = async (event) => {
    event.preventDefault();
    const { success, message } = await updateConfigType(updatedType);
    // resetAndAddTypeData();
    // setShowToast({ show: true, message, Type: success ? "info" : "danger" });
  };

  return (
    <Modal show={showUpdateTypeModal}>
      <Modal.Header closeButton>
        <Modal.Title>Any Change?</Modal.Title>
      </Modal.Header>
      <Form onSubmit={onSubmit}>
        <Modal.Body>
          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Type"
              name="type"
              required
              aria-describedby="title-help"
              value={type}
              onChange={onChangeUpdatedTypeForm}
            />
            <Form.Text id="title-help" muted>
              Required
            </Form.Text>
          </Form.Group>
          <Form.Group>
            <Form.Control
              type="number"
              step="0.01"
              placeholder="TDS value"
              name="TDS"
              value={TDS}
              onChange={onChangeUpdatedTypeForm}
            />
          </Form.Group>
          <Form.Group>
            <Form.Control
              type="number"
              step="0.01"
              placeholder="TDS dead value"
              name="dead_TDS"
              value={dead_TDS}
              onChange={onChangeUpdatedTypeForm}
            />
          </Form.Group>
          <Form.Group>
            <Form.Control
              type="number"
              step="0.01"
              placeholder="PH value"
              name="PH"
              value={PH}
              onChange={onChangeUpdatedTypeForm}
            />
          </Form.Group>
          <Form.Group>
            <Form.Control
              type="number"
              step="0.01"
              placeholder="PH dead value"
              name="dead_PH"
              value={dead_PH}
              onChange={onChangeUpdatedTypeForm}
            />
          </Form.Group>
          <Form.Group>
            <Form.Control
              type="number"
              step="0.01"
              placeholder="Nutri ratio value"
              name="nutri_Ratio"
              value={nutri_Ratio}
              onChange={onChangeUpdatedTypeForm}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary">Cancel</Button>
          <Button variant="primary" type="submit">
            Set
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

export default UpdateTypeModal;
