import React from "react";
import "./statistics.css";
import axios from "axios";
import ReactApexChart from "react-apexcharts";
import Select from "react-select";
import { url } from "../contexts/constants";
import { useState, useEffect } from "react";
import { DeviceContext } from "../contexts/DeviceContext";

const Statistics = () => {
  const arrayTime = [];
  let seriesTemp = [];
  let seriesTDS = [];
  let seriesPH = [];
  let optionsDevice = [];

  const {
    deviceState: { deviceData },
    getDevice,
  } = useContext(DeviceContext);
  // //get last data
  useEffect(() => getDevice(), []);
  // axios
  //   .get(`${url}/device`)
  //   .then((response) => {
  //     if (response) {
  //       setTimeout(function () {
  //         const data = response.data;

  //         optionsDevice.push(...data.getalldata);
  //       }, 1000);
  //     }

  //     // for (let i = 0; i < data.getalldata.length; i++) {
  //     //   let value = {
  //     //     value: data.getalldata[i].device,
  //     //     label: `Device ${i + 1}`,
  //     //   };
  //     //   optionsDevice.push(...value);
  //     // }
  //   })
  //   .catch(() => {
  //     console.log("Error retrieving data!!!");
  //   });

  const [selectDevice, setselectDevice] = useState("Device 2");
  const OnChangeDevice = (event) => {
    setselectDevice(event.label);
  };

  console.log(optionsDevice);

  var selectedDevice = null;

  // for (let i = 0; i < 2; i++) {
  //   console.log(optionsDevice[i].label);
  //   if (selectDevice === optionsDevice[i].label) {
  //     selectedDevice = optionsDevice[i].value;
  //     console.log(optionsDevice[i].value);
  //   }
  // }

  const [time, setTime] = useState("1");
  if (time === "1") {
    console.log(selectedDevice);
    fetchChartData("58bf251706a0", 48);
  } else if (time === "2") {
    console.log(selectedDevice);
    fetchChartData("58bf251706a0", 336);
  } else if (time === "3") {
    console.log(selectedDevice);
    fetchChartData("58bf251706a0", 1440);
  }
  function fetchChartData(device, length) {
    axios
      .get(`${url}/data/chart/${device}/${length}`)
      .then((response) => {
        const data = response.data;

        seriesTemp.push(...data.getalldata[0]);
        seriesTDS.push(...data.getalldata[1]);
        seriesPH.push(...data.getalldata[2]);
        arrayTime.push(...data.getalldata[3]);

        window.dispatchEvent(new Event("resize"));
      })
      .catch(() => {
        console.log("Error retrieving data!!!");
      });
  }

  const seriesArea = [
    {
      name: "Temperature",
      data: seriesTemp,
    },
  ];
  const optionsArea = {
    chart: {
      id: "area",
      group: "social",
      type: "area",
    },
    colors: ["#00E396"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
    },
    zoom: {
      type: "x",
      enabled: true,
      autoScaleYaxis: true,
    },
    toolbar: {
      autoSelected: "zoom",
    },
  };

  const seriesSmall = [
    {
      name: "PH",
      data: seriesPH,
    },
  ];
  const optionsSmall = {
    chart: {
      id: "ig",
      group: "social",
      type: "area",
    },
    colors: ["#008FFB"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
      tickAmount: 6,
    },
    zoom: {
      type: "x",
      enabled: true,
      autoScaleYaxis: true,
    },
    toolbar: {
      autoSelected: "zoom",
    },
  };

  const seriesSmall2 = [
    {
      name: "TDS",
      data: seriesTDS,
    },
  ];
  const optionsSmall2 = {
    chart: {
      id: "li",
      group: "social",
      type: "area",
    },
    colors: ["#b37700"],
    xaxis: {
      type: "datetime",
      min: arrayTime[0],
      tickAmount: 6,
    },
    zoom: {
      type: "x",
      enabled: true,
      autoScaleYaxis: true,
    },
    toolbar: {
      autoSelected: "zoom",
    },
  };
  const options = [
    { value: "1", label: "1 day" },
    { value: "2", label: "1 week" },
    { value: "3", label: "1 month" },
  ];

  const onChange = (event) => setTime(event.value);

  return (
    <>
      <div id="wrapper" className="wrapper">
        <Select
          options={optionsDevice}
          className="selecttime"
          placeholder={<div>{selectDevice}</div>}
          onChange={OnChangeDevice}
        />
        <Select
          options={options}
          className="selecttime"
          placeholder={<div>1 day</div>}
          onChange={onChange}
        />
        <div className="chart-time-series">
          <div id="chart-small" className="timeseries">
            <ReactApexChart
              options={optionsSmall}
              series={seriesSmall}
              type="area"
              height={170}
              width={1040}
            />
            <h3>PH Time Series Chart</h3>
          </div>
          <div id="chart-small2" className="timeseries">
            <ReactApexChart
              options={optionsSmall2}
              series={seriesSmall2}
              type="area"
              height={170}
              width={1040}
            />
            <h3>TDS Time Series Chart</h3>
          </div>

          <div>
            <div id="chart-area" className="timeseries">
              <ReactApexChart
                options={optionsArea}
                series={seriesArea}
                type="area"
                height={170}
                width={1040}
              />
              <h3>Temperature Time Series Chart</h3>
            </div>
          </div>
        </div>
      </div>
      <div className="wrapper"></div>
    </>
  );
};

export default Statistics;
