import React from "react";
import "./statistics.css";
import axios from "axios";
import ReactApexChart from "react-apexcharts";
import Select from "react-select";
import { url } from "../contexts/constants";
import { useState, useContext, useEffect } from "react";
import { DeviceContext } from "../contexts/DeviceContext";

const Statistics = () => {
  // const arrayTemp = [];
  // const arrayTDS = [];
  // const arrayPH = [];
  // const arrayTime = [];
  // let seriesTemp = [];
  // let seriesTDS = [];
  // let seriesPH = [];
  // let dataseries = [];
  const [seriesTemp, setSeriesTemp] = useState([]);
  const [seriesTDS, setSeriesTDS] = useState([]);
  const [seriesPH, setSeriesPH] = useState([]);
  const [arrayTime, setArrayTime] = useState([]);

  const {
    deviceState: { deviceData },
    getDevice,
  } = useContext(DeviceContext);
  // //get last data
  useEffect(() => getDevice(), []);

  function fetchChartData(time, select) {
    axios
      .get(`${url}/data/chart/${select}/${time}`)
      .then((response) => {
        const data = response.data;
        setSeriesTDS(data.getalldata[1]);
        setSeriesPH(data.getalldata[2]);
        setSeriesTemp(data.getalldata[0]);
        setArrayTime(data.getalldata[3]);
        // arrayTime.push(...data.getalldata[3]);
        // seriesTemp.push(...data.getalldata[0]);
        // seriesTDS.push(...data.getalldata[1]);
        // seriesPH.push(...data.getalldata[2]);
        // window.dispatchEvent(new Event("resize"));
        setSeriesTemp({
          seriesArea: [
            {
              name: "Temperature",
              data: seriesTemp,
            },
          ],
          optionsArea: {
            chart: {
              id: "area",
              group: "social",
              type: "area",
            },
            colors: ["#00E396"],
            xaxis: {
              type: "datetime",
              categories: arrayTime,
            },
          },
        });
        setSeriesPH({
          seriesSmall: [
            {
              name: "PH",
              data: seriesPH,
            },
          ],
          optionsSmall: {
            chart: {
              id: "ig",
              group: "social",
              type: "area",
            },
            colors: ["#008FFB"],
            xaxis: {
              type: "datetime",
              categories: arrayTime,
              tickAmount: 6,
            },
          },
        });
        setSeriesTDS({
          seriesSmall2: [
            {
              name: "TDS",
              data: seriesTDS,
            },
          ],
        });
      })
      .catch(() => {
        console.log("Error retrieving data!!!");
      });
  }

  console.log(seriesTemp);
  console.log(seriesPH);
  console.log(seriesTDS);
  console.log(arrayTime);
  var optionsDevice = [];
  for (let i = 0; i < deviceData.length; i++) {
    let value = [];
    value = { value: deviceData[i].device, label: `Device ${i + 1}` };
    optionsDevice.push(value);
  }
  console.log(optionsDevice);
  const [selectDevice, setselectDevice] = useState("Device 1");

  const OnChangeDevice = (event) => {
    setselectDevice(event.label);
  };
  console.log(selectDevice);
  var selectedDevice;

  for (let i = 0; i < deviceData.length; i++) {
    if (selectDevice === optionsDevice[i].label) {
      selectedDevice = deviceData[i].device;
    }
  }

  const [time, setTime] = useState("1");
  const onChange = (event) => setTime(event.value);
  if (time === "1") {
    fetchChartData(48, selectedDevice);
    //window.dispatchEvent(new Event("resize"));
  } else if (time === "2") {
    fetchChartData(336, selectedDevice);
    //window.dispatchEvent(new Event("resize"));
  } else if (time === "3") {
    fetchChartData(1440, selectedDevice);
    // window.dispatchEvent(new Event("resize"));
  }

  // const seriesArea = [
  //   {
  //     name: "Temperature",
  //     data: seriesTemp,
  //   },
  // ];
  // const optionsArea = {
  //   chart: {
  //     id: "area",
  //     group: "social",
  //     type: "area",
  //   },
  //   colors: ["#00E396"],
  //   xaxis: {
  //     type: "datetime",
  //     categories: arrayTime,
  //   },
  // };

  // const seriesSmall = [
  //   {
  //     name: "PH",
  //     data: seriesPH,
  //   },
  // ];
  // const optionsSmall = {
  //   chart: {
  //     id: "ig",
  //     group: "social",
  //     type: "area",
  //   },
  //   colors: ["#008FFB"],
  //   xaxis: {
  //     type: "datetime",
  //     categories: arrayTime,
  //     tickAmount: 6,
  //   },
  // };

  const seriesSmall2 = [
    {
      name: "TDS",
      data: seriesTDS,
    },
  ];
  const optionsSmall2 = {
    chart: {
      id: "li",
      group: "social",
      type: "area",
    },
    colors: ["#b37700"],
    xaxis: {
      type: "datetime",
      categories: arrayTime,
      tickAmount: 6,
    },
  };
  const options = [
    { value: "1", label: "1 day" },
    { value: "2", label: "1 week" },
    { value: "3", label: "1 month" },
  ];

  return (
    <>
      <div id="wrapper" className="wrapper">
        <div className="select">
          <Select
            options={optionsDevice}
            className="selecttime"
            placeholder={<div>{selectDevice}</div>}
            onChange={OnChangeDevice}
          />
          <Select
            options={options}
            className="selecttime"
            placeholder={<div>1 day</div>}
            onChange={onChange}
          />
        </div>

        <div className="chart-time-series">
          <div id="chart-small" className="timeseries">
            <ReactApexChart
              options={optionsSmall}
              series={seriesSmall}
              type="area"
              height={170}
              width={1040}
            />
            <h3>PH Time Series Chart</h3>
          </div>
          <div id="chart-small2" className="timeseries">
            <ReactApexChart
              options={optionsSmall2}
              series={seriesSmall2}
              type="area"
              height={170}
              width={1040}
            />
            <h3>TDS Time Series Chart</h3>
          </div>

          <div>
            <div id="chart-area" className="timeseries">
              <ReactApexChart
                options={optionsArea}
                series={seriesArea}
                type="area"
                height={170}
                width={1040}
              />
              <h3>Temperature Time Series Chart</h3>
            </div>
          </div>
        </div>
      </div>
      <div className="wrapper"></div>
    </>
  );
};

export default Statistics;
